import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  Home, 
  FileText, 
  Search, 
  BarChart3, 
  Settings, 
  Sun, 
  Moon, 
  Monitor,
  Bell,
  User
} from 'lucide-react';

const Navbar = ({ darkMode, toggleDarkMode }) => {
  const location = useLocation();
  const [themeMode, setThemeMode] = useState('system');

  // ÌÖåÎßà Ï¥àÍ∏∞Ìôî
  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') || 'system';
    setThemeMode(savedTheme);
    applyTheme(savedTheme);
  }, []);

  // ÌÖåÎßà Ï†ÅÏö© Ìï®Ïàò
  const applyTheme = (mode) => {
    if (mode === 'dark') {
      document.documentElement.classList.add('dark');
    } else if (mode === 'light') {
      document.documentElement.classList.remove('dark');
    } else {
      // system Î™®Îìú
      if (window.matchMedia('(prefers-color-scheme: dark)').matches) {
        document.documentElement.classList.add('dark');
      } else {
        document.documentElement.classList.remove('dark');
      }
    }
  };

  // ÌÖåÎßà Î≥ÄÍ≤Ω Ìï∏Îì§Îü¨
  const handleThemeChange = (mode) => {
    setThemeMode(mode);
    localStorage.setItem('theme', mode);
    applyTheme(mode);
  };

  const navItems = [
    { path: '/', label: 'Ìôà', icon: Home, emoji: 'üè†' },
    { path: '/notes', label: 'ÎÇ¥ ÎÖ∏Ìä∏', icon: FileText, emoji: 'üìù' },
    { path: '/explore', label: 'Ï∂îÏ≤ú', icon: Search, emoji: 'üîç' },
    { path: '/reports', label: 'Î¶¨Ìè¨Ìä∏', icon: BarChart3, emoji: 'üìä' },
    { path: '/settings', label: 'ÏÑ§Ï†ï', icon: Settings, emoji: '‚öôÔ∏è' },
  ];

  const isActivePath = (path) => location.pathname === path;

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-white/95 dark:bg-gray-900/95 backdrop-blur-sm border-b border-gray-200 dark:border-gray-700">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Î°úÍ≥† */}
          <Link to="/" className="flex items-center space-x-3">
            <div className="w-8 h-8 bg-primary-600 rounded-lg flex items-center justify-center">
              <span className="text-white font-bold text-sm">N</span>
            </div>
            <div className="flex flex-col">
              <span className="text-lg font-bold text-gray-900 dark:text-white">ÎÖ∏Ìä∏ Ïä§ÌÖåÏù¥ÏÖò</span>
              <span className="text-xs text-gray-500 dark:text-gray-400">Ï¥àÍ∞úÏù∏Ìôî AI ÌÅêÎ†àÏù¥ÏÖò</span>
            </div>
          </Link>

          {/* ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò Î©îÎâ¥ */}
          <div className="hidden md:flex items-center space-x-1">
            {navItems.map((item) => {
              return (
                <Link
                  key={item.path}
                  to={item.path}
                  className={`flex items-center space-x-2 px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                    isActivePath(item.path)
                      ? 'bg-primary-100 text-primary-700 dark:bg-primary-900/30 dark:text-primary-300'
                      : 'text-gray-600 hover:text-gray-900 dark:text-gray-300 dark:hover:text-white hover:bg-gray-100 dark:hover:bg-gray-800'
                  }`}
                >
                  <span className="text-base">{item.emoji}</span>
                  <span>{item.label}</span>
                </Link>
              );
            })}
          </div>

          {/* Í≤ÄÏÉâÎ∞î */}
          <div className="hidden lg:flex items-center flex-1 max-w-md mx-8">
            <div className="relative w-full">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
              <input
                type="text"
                placeholder="ÎÖ∏Ìä∏, ÏΩòÌÖêÏ∏†, Ïù∏ÏÇ¨Ïù¥Ìä∏ Í≤ÄÏÉâ..."
                className="w-full pl-10 pr-4 py-2 bg-gray-100 dark:bg-gray-800 border-0 rounded-lg text-sm focus:ring-2 focus:ring-primary-500 focus:bg-white dark:focus:bg-gray-700 transition-colors text-gray-900 dark:text-white"
              />
            </div>
          </div>

          {/* Ïö∞Ï∏° Ïª®Ìä∏Î°§ */}
          <div className="flex items-center space-x-2">
            {/* ÌÖåÎßà ÌÜ†Í∏Ä */}
            <div className="flex items-center bg-gray-100 dark:bg-gray-800 rounded-lg p-1">
              <button
                onClick={() => handleThemeChange('light')}
                className={`p-1.5 rounded-md transition-colors ${
                  themeMode === 'light' 
                    ? 'bg-white dark:bg-gray-700 shadow-sm text-gray-900 dark:text-white' 
                    : 'hover:bg-gray-200 dark:hover:bg-gray-700 text-gray-600 dark:text-gray-400'
                }`}
                title="ÎùºÏù¥Ìä∏ Î™®Îìú"
              >
                <Sun className="w-4 h-4" />
              </button>
              <button
                onClick={() => handleThemeChange('dark')}
                className={`p-1.5 rounded-md transition-colors ${
                  themeMode === 'dark' 
                    ? 'bg-gray-700 shadow-sm text-white' 
                    : 'hover:bg-gray-200 dark:hover:bg-gray-700 text-gray-600 dark:text-gray-400'
                }`}
                title="Îã§ÌÅ¨ Î™®Îìú"
              >
                <Moon className="w-4 h-4" />
              </button>
              <button
                onClick={() => handleThemeChange('system')}
                className={`p-1.5 rounded-md transition-colors ${
                  themeMode === 'system' 
                    ? 'bg-white dark:bg-gray-700 shadow-sm text-gray-900 dark:text-white' 
                    : 'hover:bg-gray-200 dark:hover:bg-gray-700 text-gray-600 dark:text-gray-400'
                }`}
                title="ÏãúÏä§ÌÖú ÏÑ§Ï†ï Îî∞Î•¥Í∏∞"
              >
                <Monitor className="w-4 h-4" />
              </button>
            </div>

            {/* ÏïåÎ¶º */}
            <button className="p-2 text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
              <Bell className="w-5 h-5" />
            </button>

            {/* ÌîÑÎ°úÌïÑ */}
            <button className="p-2 text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-800 rounded-lg transition-colors">
              <User className="w-5 h-5" />
            </button>
          </div>
        </div>
      </div>

      {/* Î™®Î∞îÏùº ÎÑ§ÎπÑÍ≤åÏù¥ÏÖò */}
      <div className="md:hidden bg-white dark:bg-gray-900 border-t border-gray-200 dark:border-gray-700">
        <div className="flex items-center justify-around py-2">
          {navItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`flex flex-col items-center space-y-1 px-3 py-2 text-xs font-medium transition-colors ${
                isActivePath(item.path)
                  ? 'text-primary-600 dark:text-primary-400'
                  : 'text-gray-600 dark:text-gray-400'
              }`}
            >
              <span className="text-lg">{item.emoji}</span>
              <span>{item.label}</span>
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
