import React, { useState } from 'react';
import { 
  FileText, 
  Plus, 
  Search, 
  Filter, 
  Calendar,
  Tag,
  Star,
  MoreVertical,
  Edit,
  Trash2,
  Clock,
  BookOpen,
  TrendingUp,
  Users
} from 'lucide-react';

const Notes = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [showNewNoteModal, setShowNewNoteModal] = useState(false);
  const [newNoteTitle, setNewNoteTitle] = useState('');
  const [newNoteContent, setNewNoteContent] = useState('');

  // Í∞ÄÏÉÅ ÎÖ∏Ìä∏ Îç∞Ïù¥ÌÑ∞
  const notes = [
    {
      id: 1,
      title: 'Ï∞ΩÏùòÏÑ±Ïóê ÎåÄÌïú Í≥†ÎØº',
      content: 'Ï∞ΩÏùòÏ†ÅÏù∏ ÏïÑÏù¥ÎîîÏñ¥Í∞Ä ÎÇòÏò§ÏßÄ ÏïäÏùÑ ÎïåÏùò ÎãµÎãµÌï®Í≥º Í∑∏Í≤ÉÏùÑ Í∑πÎ≥µÌïòÎäî Î∞©Î≤ïÏóê ÎåÄÌï¥ ÏÉùÍ∞ÅÌï¥Î≥¥ÏïòÎã§. ÎïåÎ°úÎäî Ìú¥ÏãùÏù¥ ÌïÑÏöîÌïòÍ≥†, ÏÉàÎ°úÏö¥ ÌôòÍ≤ΩÏóêÏÑúÏùò ÏòÅÍ∞êÏù¥ Ï§ëÏöîÌïòÎã§Îäî Í≤ÉÏùÑ Íπ®Îã¨ÏïòÎã§.',
      tags: ['Ï∞ΩÏùòÏÑ±', 'Î¨∏Ï†úÌï¥Í≤∞', 'ÏÑ±Ïû•'],
      emoji: 'ü§î',
      importance: 85,
      relatedNotes: 3,
      createdAt: '12Ïùº Ï†Ñ',
      lastModified: '5Ïùº Ï†Ñ',
      starred: true,
      readTime: 3
    },
    {
      id: 2,
      title: 'ÏÉàÎ°úÏö¥ ÎèÑÏ†ÑÏóê ÎåÄÌïú ÎëêÎ†§ÏõÄ',
      content: 'ÏïàÏ†ÑÌïú Í∏∏Í≥º ÎèÑÏ†ÑÏ†ÅÏù∏ Í∏∏ ÏÇ¨Ïù¥ÏóêÏÑúÏùò Í≥†ÎØº, Ïã§Ìå®Ïóê ÎåÄÌïú ÎëêÎ†§ÏõÄÏùÑ Ïñ¥ÎñªÍ≤å Í∑πÎ≥µÌï† Í≤ÉÏù∏Í∞Ä? ÏûëÏùÄ ÏÑ±Ï∑®Îì§ÏùÑ ÏåìÏïÑÍ∞ÄÎ©∞ ÏûêÏã†Í∞êÏùÑ ÌÇ§ÏõåÎÇòÍ∞ÄÎäî Í≤ÉÏù¥ Ï§ëÏöîÌïòÎã§.',
      tags: ['ÎèÑÏ†Ñ', 'ÎëêÎ†§ÏõÄ', 'ÏÑ±Ïû•', 'ÏûêÏã†Í∞ê'],
      emoji: 'üò∞',
      importance: 92,
      relatedNotes: 5,
      createdAt: '8Ïùº Ï†Ñ',
      lastModified: '2Ïùº Ï†Ñ',
      starred: true,
      readTime: 4
    },
    {
      id: 3,
      title: 'Ïò§ÎäòÏùò ÏòÅÍ∞ê ÎÖ∏Ìä∏',
      content: 'ÏÇ∞Ï±Ö Ï§ëÏóê Îñ†Ïò§Î•∏ ÏïÑÏù¥ÎîîÏñ¥Îì§ÏùÑ Îπ†Î•¥Í≤å Í∏∞Î°ùÌïúÎã§. ÏûêÏó∞ÏóêÏÑú ÏñªÎäî ÌèâÏò®Ìï®Í≥º ÏÉàÎ°úÏö¥ Í¥ÄÏ†êÎì§Ïù¥ ÎßàÏùåÏùÑ Ï†ïÎ¶¨ÌïòÎäîÎç∞ ÎèÑÏõÄÏù¥ ÎêúÎã§.',
      tags: ['ÏòÅÍ∞ê', 'ÏïÑÏù¥ÎîîÏñ¥', 'ÏûêÏó∞', 'ÌèâÏò®'],
      emoji: 'üí°',
      importance: 78,
      relatedNotes: 2,
      createdAt: '1Ïùº Ï†Ñ',
      lastModified: '1Ïùº Ï†Ñ',
      starred: false,
      readTime: 2
    },
    {
      id: 4,
      title: 'ÌïôÏäµ Î∞©Î≤ïÎ°† Ï†ïÎ¶¨',
      content: 'Ìö®Í≥ºÏ†ÅÏù∏ ÌïôÏäµÏùÑ ÏúÑÌïú Ïó¨Îü¨ Î∞©Î≤ïÎ°†Îì§ÏùÑ Ï†ïÎ¶¨ÌñàÎã§. Ìè¨Î™®ÎèÑÎ°ú Í∏∞Î≤ï, Îä•ÎèôÏ†Å Î≥µÏäµ, ÍµêÏ∞® ÌïôÏäµ Îì±Ïù¥ ÌäπÌûà Ìö®Í≥ºÏ†ÅÏûÑÏùÑ ÌôïÏù∏ÌñàÎã§.',
      tags: ['ÌïôÏäµ', 'Î∞©Î≤ïÎ°†', 'Ìö®Ïú®ÏÑ±'],
      emoji: 'üìö',
      importance: 88,
      relatedNotes: 4,
      createdAt: '3Ïùº Ï†Ñ',
      lastModified: '1Ïùº Ï†Ñ',
      starred: true,
      readTime: 5
    },
    {
      id: 5,
      title: 'ÎßàÏùåÏ±ôÍπÄ Ïó∞Ïäµ ÏùºÏßÄ',
      content: 'Î™ÖÏÉÅÍ≥º ÎßàÏùåÏ±ôÍπÄ Ïó∞ÏäµÏùÑ ÌÜµÌï¥ ÏñªÏùÄ Ïù∏ÏÇ¨Ïù¥Ìä∏Îì§ÏùÑ Í∏∞Î°ùÌïúÎã§. ÌòÑÏû¨ ÏàúÍ∞ÑÏóê ÏßëÏ§ëÌïòÎäî Í≤ÉÏùò Ï§ëÏöîÏÑ±Í≥º Í∞êÏ†ïÏùÑ Í¥ÄÏ∞∞ÌïòÎäî Î∞©Î≤ïÎì§.',
      tags: ['ÎßàÏùåÏ±ôÍπÄ', 'Î™ÖÏÉÅ', 'Í∞êÏ†ï', 'ÌòÑÏû¨'],
      emoji: 'üßò',
      importance: 82,
      relatedNotes: 3,
      createdAt: '6Ïùº Ï†Ñ',
      lastModified: '4Ïùº Ï†Ñ',
      starred: false,
      readTime: 3
    }
  ];

  // ÌïÑÌÑ∞ÎßÅÎêú ÎÖ∏Ìä∏
  const filteredNotes = notes.filter(note => {
    const matchesSearch = note.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         note.content.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         note.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));
    
    if (selectedFilter === 'all') return matchesSearch;
    if (selectedFilter === 'starred') return matchesSearch && note.starred;
    if (selectedFilter === 'recent') return matchesSearch && note.createdAt.includes('Ïùº');
    return matchesSearch;
  });

  // ÏÉà ÎÖ∏Ìä∏ ÏÉùÏÑ±
  const handleCreateNote = () => {
    if (newNoteTitle.trim() && newNoteContent.trim()) {
      // Ïó¨Í∏∞ÏÑú Ïã§Ï†úÎ°úÎäî API Ìò∏Ï∂ú
      console.log('ÏÉà ÎÖ∏Ìä∏ ÏÉùÏÑ±:', { title: newNoteTitle, content: newNoteContent });
      setNewNoteTitle('');
      setNewNoteContent('');
      setShowNewNoteModal(false);
    }
  };

  const filters = [
    { key: 'all', label: 'Ï†ÑÏ≤¥', count: notes.length },
    { key: 'starred', label: 'Ï¶êÍ≤®Ï∞æÍ∏∞', count: notes.filter(n => n.starred).length },
    { key: 'recent', label: 'ÏµúÍ∑º', count: notes.filter(n => n.createdAt.includes('Ïùº')).length }
  ];

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Ìó§Îçî */}
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center space-x-3">
            <FileText className="w-8 h-8 text-blue-600" />
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white">ÎÇ¥ ÎÖ∏Ìä∏</h1>
              <p className="text-gray-600 dark:text-gray-400">ÏÉùÍ∞ÅÏùÑ Í∏∞Î°ùÌïòÍ≥† Ï†ïÎ¶¨ÌïòÏÑ∏Ïöî</p>
            </div>
          </div>
          <button 
            onClick={() => setShowNewNoteModal(true)}
            className="btn-primary flex items-center space-x-2"
          >
            <Plus className="w-4 h-4" />
            <span>ÏÉà ÎÖ∏Ìä∏</span>
          </button>
        </div>

        {/* Í≤ÄÏÉâ Î∞è ÌïÑÌÑ∞ */}
        <div className="card p-6 mb-8">
          <div className="flex flex-col md:flex-row gap-4">
            {/* Í≤ÄÏÉâÎ∞î */}
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
              <input
                type="text"
                placeholder="ÎÖ∏Ìä∏ Ï†úÎ™©, ÎÇ¥Ïö©, ÌÉúÍ∑∏Î°ú Í≤ÄÏÉâ..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 bg-gray-100 dark:bg-gray-800 border-0 rounded-lg text-sm focus:ring-2 focus:ring-primary-500 focus:bg-white dark:focus:bg-gray-700 transition-colors text-gray-900 dark:text-white"
              />
            </div>

            {/* ÌïÑÌÑ∞ Î≤ÑÌäºÎì§ */}
            <div className="flex items-center space-x-2">
              {filters.map((filter) => (
                <button
                  key={filter.key}
                  onClick={() => setSelectedFilter(filter.key)}
                  className={`px-4 py-2 rounded-lg text-sm font-medium transition-colors flex items-center space-x-2 ${
                    selectedFilter === filter.key
                      ? 'bg-primary-100 text-primary-700 dark:bg-primary-900/30 dark:text-primary-300'
                      : 'bg-gray-100 hover:bg-gray-200 dark:bg-gray-800 dark:hover:bg-gray-700 text-gray-700 dark:text-gray-300'
                  }`}
                >
                  <span>{filter.label}</span>
                  <span className="bg-gray-200 dark:bg-gray-600 text-xs px-2 py-0.5 rounded-full">
                    {filter.count}
                  </span>
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* ÎÖ∏Ìä∏ Í∑∏Î¶¨Îìú */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredNotes.map((note) => (
            <div key={note.id} className="card p-6 hover:shadow-md transition-shadow group">
              {/* ÎÖ∏Ìä∏ Ìó§Îçî */}
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center space-x-3">
                  <span className="text-2xl">{note.emoji}</span>
                  <div className="flex-1">
                    <h3 className="font-bold text-gray-900 dark:text-white group-hover:text-blue-600 transition-colors">
                      {note.title}
                    </h3>
                    <div className="flex items-center space-x-2 text-xs text-gray-500 dark:text-gray-400 mt-1">
                      <Clock className="w-3 h-3" />
                      <span>{note.createdAt}</span>
                      <span>‚Ä¢</span>
                      <span>{note.readTime}Î∂Ñ ÏùΩÍ∏∞</span>
                    </div>
                  </div>
                </div>
                
                <div className="flex items-center space-x-2">
                  {note.starred && <Star className="w-4 h-4 text-yellow-500 fill-current" />}
                  <button className="opacity-0 group-hover:opacity-100 p-1 hover:bg-gray-100 dark:hover:bg-gray-700 rounded transition-all">
                    <MoreVertical className="w-4 h-4 text-gray-500" />
                  </button>
                </div>
              </div>

              {/* ÎÖ∏Ìä∏ ÎÇ¥Ïö© */}
              <p className="text-sm text-gray-600 dark:text-gray-400 mb-4 line-clamp-3">
                {note.content}
              </p>

              {/* ÌÉúÍ∑∏ */}
              <div className="flex flex-wrap gap-1 mb-4">
                {note.tags.map((tag, index) => (
                  <span
                    key={index}
                    className="inline-flex items-center px-2 py-1 rounded-full text-xs bg-blue-100 dark:bg-blue-900/30 text-blue-700 dark:text-blue-300"
                  >
                    <Tag className="w-3 h-3 mr-1" />
                    {tag}
                  </span>
                ))}
              </div>

              {/* ÎÖ∏Ìä∏ ÌÜµÍ≥Ñ */}
              <div className="flex items-center justify-between pt-4 border-t border-gray-100 dark:border-gray-700">
                <div className="flex items-center space-x-4 text-xs text-gray-500 dark:text-gray-400">
                  <div className="flex items-center space-x-1">
                    <TrendingUp className="w-3 h-3" />
                    <span>Ï§ëÏöîÎèÑ {note.importance}%</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <Users className="w-3 h-3" />
                    <span>{note.relatedNotes}Í∞ú Ïó∞Í¥Ä</span>
                  </div>
                </div>
                
                <div className="flex items-center space-x-1">
                  <button className="p-1 hover:bg-gray-100 dark:hover:bg-gray-700 rounded transition-colors">
                    <Edit className="w-4 h-4 text-gray-500 hover:text-blue-600" />
                  </button>
                  <button className="p-1 hover:bg-gray-100 dark:hover:bg-gray-700 rounded transition-colors">
                    <Trash2 className="w-4 h-4 text-gray-500 hover:text-red-600" />
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Í≤ÄÏÉâ Í≤∞Í≥º ÏóÜÏùå */}
        {filteredNotes.length === 0 && (
          <div className="card p-12">
            <div className="text-center">
              <FileText className="w-16 h-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-2">
                {searchTerm ? 'Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§' : 'ÎÖ∏Ìä∏Í∞Ä ÏóÜÏäµÎãàÎã§'}
              </h3>
              <p className="text-gray-500 dark:text-gray-400 mb-4">
                {searchTerm ? 'Îã§Î•∏ Í≤ÄÏÉâÏñ¥Î°ú ÏãúÎèÑÌï¥Î≥¥ÏÑ∏Ïöî' : 'Ï≤´ Î≤àÏß∏ ÎÖ∏Ìä∏Î•º ÏûëÏÑ±Ìï¥Î≥¥ÏÑ∏Ïöî'}
              </p>
              <button 
                onClick={() => setShowNewNoteModal(true)}
                className="btn-primary"
              >
                ÏÉà ÎÖ∏Ìä∏ ÏûëÏÑ±
              </button>
            </div>
          </div>
        )}

        {/* ÏÉà ÎÖ∏Ìä∏ Î™®Îã¨ */}
        {showNewNoteModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white dark:bg-gray-800 rounded-lg p-6 w-full max-w-2xl">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-xl font-bold text-gray-900 dark:text-white">ÏÉà ÎÖ∏Ìä∏ ÏûëÏÑ±</h2>
                <button 
                  onClick={() => setShowNewNoteModal(false)}
                  className="text-gray-500 hover:text-gray-700 dark:text-gray-400 dark:hover:text-gray-200"
                >
                  ‚úï
                </button>
              </div>
              
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Ï†úÎ™©
                  </label>
                  <input
                    type="text"
                    value={newNoteTitle}
                    onChange={(e) => setNewNoteTitle(e.target.value)}
                    placeholder="ÎÖ∏Ìä∏ Ï†úÎ™©ÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî"
                    className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                  />
                </div>
                
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    ÎÇ¥Ïö©
                  </label>
                  <textarea
                    value={newNoteContent}
                    onChange={(e) => setNewNoteContent(e.target.value)}
                    placeholder="ÏÉùÍ∞ÅÏùÑ ÏûêÏú†Î°≠Í≤å Í∏∞Î°ùÌïòÏÑ∏Ïöî..."
                    rows={8}
                    className="w-full p-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
                  />
                </div>
              </div>
              
              <div className="flex items-center justify-end space-x-3 mt-6">
                <button 
                  onClick={() => setShowNewNoteModal(false)}
                  className="btn-secondary"
                >
                  Ï∑®ÏÜå
                </button>
                <button 
                  onClick={handleCreateNote}
                  className="btn-primary"
                >
                  Ï†ÄÏû•
                </button>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Notes;
